{% extends "notifications/email_reservation.html" %}
{% load i18n %}
{% block preheader %}{% blocktranslate %}Deine Reservierungsanfrage für {{ reservation.resource.display_name }} ist eingegangen und wird in Kürze bearbeitet.{% endblocktranslate %}{% endblock %}
{% block reservation_instruction %}Aktuell sind keine weiteren Schritte nötig. Bitte warte auf die Bearbeitung durch die Buchungsverantwortlichen.<br/>
    {{ block.super }}
    {% endblock %}

{% extends "notifications/email_reservation.html" %}
{% load i18n %}
{% block preheader %}{% blocktranslate %}Deine Reservierungsanfrage für {{ reservation.resource.display_name }} wurde bestätigt.{% endblocktranslate %}{% endblock %}
{% block reservation_instruction %}{% endblock %}

{% extends "notifications/email_reservation.html" %}
{% load i18n %}
{% block preheader %}{% blocktranslate %}Eine Reservierungsanfrage von {{ reservation.organizer.display_name }} für {{ reservation.resource.display_name }} ist eingegangen.{% endblocktranslate %}{% endblock %}

{% block reservation_status %}Bitte bearbeite folgende Reservierungsanfrage{% endblock %}

{% block reservation_instruction %}

{% endblock %}

                                        {% block call_to_action %}
                                            <table role="presentation" border="0" cellpadding="0" cellspacing="0" class="btn btn-primary">
                                              <tbody>
                                                <tr>
                                                  <td align="left">
                                                    <table role="presentation" border="0" cellpadding="0" cellspacing="0">
                                                      <tbody>
                                                        <tr>
                                                          <td> <a href="{% url 'admin:resources_reservation_change' reservation.pk %}" target="_blank">Reservierung {{ reservation.identifier }} öffnen</a> </td>
                                                        </tr>
                                                      </tbody>
                                                    </table>
                                                  </td>
                                                </tr>
                                              </tbody>
                                            </table>
                                        {% endblock %}

{% extends "notifications/email_reservation.html" %}
{% load i18n %}
{% block preheader %}{% blocktranslate %}Deine Reservierungsanfrage für {{ reservation.resource.display_name }} wurde abgesagt.{% endblocktranslate %}{% endblock %}

    {% block reservation_instruction %}
    {% endblock %}°

{% extends "notifications/email_reservation.html" %}
{% load i18n %}
{% block preheader %}{% blocktranslate %}Deine Reservierungsanfrage für {{ reservation.resource.display_name }} wurde leider abgelehnt.{% endblocktranslate %}{% endblock %}

    {% block reservation_instruction %}
{% blocktranslate %}Möglicherweise kannst Du eine neue Anfrage stellen.{% endblocktranslate %}
    {% endblock %}°

{% extends "notifications/email_reservation.html" %}
{% load i18n %}
{% block preheader %}{% blocktranslate %}Eine Anmeldung von externen Teilnehmern von {{ reservation.organizer }}  ist eingegangen.{% endblocktranslate %}{% endblock %}

{% block reservation_status %}
    <p class="primary bold box">Bitte bearbeite folgende Anmeldung von externen Teilnehmern</p>
    {% endblock %}

{% block reservation_instruction %}

{% endblock %}

