                                            <table role="presentation" border="0" cellpadding="0" cellspacing="0" class="btn btn-primary">
                                              <tbody>
                                                <tr>
                                                  <td align="left">
                                                    <table role="presentation" border="0" cellpadding="0" cellspacing="0">
                                                      <tbody>
                                                        <tr>
                                                          <td> <a href="{% url 'admin:resources_reservation_change' reservation.pk %}" target="_blank">Reservierung {{ reservation.identifier }} öffnen</a> </td>
